div.script:
// Get all the checkboxes and the table rows
const category1Checkbox = document.querySelector('.category1Filter');
const category2Checkbox = document.querySelector('.category2Filter');
const category1Rows = document.querySelectorAll('.category1');
const category2Rows = document.querySelectorAll('.category2');

// Function to toggle the rows visibility
function toggleRows() {
  category1Rows.forEach(row => {
    row.style.display = category1Checkbox.checked ? '' : 'none';
  });
  category2Rows.forEach(row => {
    row.style.display = category2Checkbox.checked ? '' : 'none';
  });
}

// Add event listeners to checkboxes
category1Checkbox.addEventListener('change', toggleRows);
category2Checkbox.addEventListener('change', toggleRows);

// Initialize visibility based on the initial state of the checkboxes
toggleRows();
